            // I2C主机总线资源分配，I2C PORT0
            TiI2CMaster i2cm0 = TiI2CMaster.open(0);
            // I2C主机总线资源与屏幕对象绑定，屏幕地址：0x3C           
            TiOLED_UG2864 oled = new TiOLED_UG2864(i2cm0, 0x3c);
            // GPIO总线资源与红外解码对象绑定
            TiVS1838BNEC vs1838b = new TiVS1838BNEC(gpio0, 5);
            // 屏幕开启并清屏
            oled.turnOn();
            oled.clear();
            // 显示标题
            oled.print(0, 0, "IRDecode.");
