    TiADC _adc;
    TiOLED_UG2864 _oled;

    // 构造
    public EMICThread(TiADC adc, TiOLED_UG2864 oled) {
        this._adc = adc;
        this._oled = oled;
    }

    @Override
    public void run() {

        try {
            while (true) {
                // 获取咪头(MIC)电压
                int val = this._adc.getRawValue(0);
                // 显示咪头(MIC)电压
                this._oled.print(2, 0, "MIC: " + val + "    ");
                Delay.msDelay(50);
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

}

